import api from './service/api';
export default {
  data() {
    return {
      link: process.env.VUE_APP_ASSESSMENT,
      info: []
    };
  },

  mounted() {
    this.read();
  },

  methods: {
    async read() {
      await api.get(this.link + '/hotel/read').then(res => {
        this.info = res.data, console.log(res.data);
      });
    },

    async create() {
      this.flag = true;
      this.dialog = true;
      await api.post(this.link + '/createDept', this.department).then(res => {
        this.info = res.data;
      });
      this.dialog = false;
      await this.read();
    },

    edit(item) {
      this.flag = false;
      this.dialog = true;
      this.department = {
        deptId: item.dept_id,
        deptName: item.dept_name,
        location: item.location
      };
    },

    async updateItem() {
      await api.update(this.link + '/updateDept', this.department).then(res => {
        this.info = res.data;
      });
      this.dialog = false;
      await this.read(), this.flag = true;
    },

    async del(id) {
      await api.del(this.link + '/delete/' + id);
      await this.read();
    },

    async searching(resp) {
      await api.post(this.link + '/searchDept', resp).then(res => {
        this.info = res.data;
      });
    },

    async asc(val, item) {
      await api.post(this.link + val, item).then(res => {
        this.info = res.data;
      });
    },

    async desc(val, item) {
      await api.post(this.link + val, item).then(res => {
        this.info = res.data;
      });
    }

  }
};
